import React from 'react';
import $ from "jquery";
import localStorage from 'localStorage'

export default class Example extends React.Component {


    constructor(props) {
        super(props);

        this.toggle = this.toggle.bind(this);
        this.state = {
            isOpen: false,
        };
    }
    toggle() {
        this.setState({
            isOpen: !this.state.isOpen
        });

    }

    componentWillMount() {
        let endpoint_API = "http://quyzygy.us/";

        $(window).on('load', function () {
            $.ajax({
                url: endpoint_API + "myQuizzes?sk=" + localStorage.getItem('sk'),
                type: "GET",
                crossDomain: true,
                success: function (response) {
                    console.log(response)
                    $.each(response, function (key, value) {
                        $('tbody').append(`<tr>
                            <td class="id"><div id="#id" contenteditable>${value.id}</div></td>
                            <td class="Quiz name"><div id="#qText01" contenteditable>${value.QuizName}</div></td>
                            <td class="Course name"><div id="#qText02" contentEditable>${value.CourseName}</div></td>
                            <td class="Duration"><div id="#qText03" contentEditable>${value.Duration}</div></td>
                            <td class="Questions"><div id="#qText05" contentEditable>${value.Questions}</div></td>
                         </tr>`);
                    })
                },
                error: function (xhr, status) {
                    alert("error");
                }
            });


            $("#qText00").val(localStorage.getItem('email'));
            $("#save").on('click', function () {
                $.ajax({
                    url: endpoint_API + "updateQuiz?sk=" + localStorage.getItem('sk'),
                    type: "POST",
                    dataType: 'json',
                    contentType: 'application/json',
                    data: JSON.stringify({ id:$("#id").val(), Author:localStorage.getItem('email'), CourseName: $("#qText01").val(), QuizName: $("#qText02").val(),Duration:(parseInt($("#qText03").val()) * 60),Questions: $("#qText05").val()}),
                    crossDomain: true,
                    success: function (response) {
                        console.log(response)
                    },
                    error: function (xhr, status) {
                        alert("error");
                    }
                });
            });

        });

    }


    render() {
        if (window.location.href.indexOf('reload')===-1) {
            window.location.replace(window.location.href+'?reload');
        }
        return (

            <div>
                <h2>My Quizes</h2>
                <div>
                    <input type="text" id="qText00" disabled/>
                </div>
                <table border="1">
                    <tbody>
                    <tr>
                        <th> Quiz name</th>
                        <th> Quiz name</th>
                        <th> Course Name</th>
                        <th> Duration</th>
                        <th> Questions</th>
                    </tr>
                    </tbody>
                </table>

                <input className='btnClk createQBT' type="button" value="save" id="save"/>
            </div>
        )

    }

}
